import React from "react";

const Todo = (props) => {

  const { todoDetails, toggleCompleteStatus, removeTodo } = props;

const setComplete = () => {
  if(todoDetails.complete) {
    return "completed"
  }
}

  const handleTogglingClick = () => {
    toggleCompleteStatus(todoDetails.id);
  };

  const handleRemoveClick = () => {
    removeTodo(todoDetails.id);
  };

  return (
    <div className={`todos-wrapper`} onClick={handleTogglingClick}>
      <h3 className={`todo-text ${setComplete()}`} >{todoDetails.name}</h3>
      <i className={`fa fa-check-circle-o fa-2x`}/>
      <i id="delete-btn" className={`fa fa-times-circle-o fa-2x`} onClick={handleRemoveClick}>
      </i>
    </div>
  );
};

export default Todo;
