import React, {useState} from "react";

const Todo = (props) => {
  const [hovered, setHovered] = useState(false);

  const { todoDetails, toggleCompleteStatus, removeTodo } = props;

const setComplete = () => {
  if(todoDetails.complete) {
    return "completed"
  } else return "incomplete"
}

const renderDeleteButton = () => {
  if (hovered) {
  return (
    <i id="delete-btn" className={`fas fa-window-close fa-2x`} onClick={handleRemoveClick}></i>
  )}
}

  const handleTogglingClick = () => {
    toggleCompleteStatus(todoDetails.id);
  };

  const handleRemoveClick = () => {
    removeTodo(todoDetails.id);
  };

  return (
    <div className={`todos-wrapper`} onMouseEnter={setHovered(true)} onMouseLeave={setHovered(false)}>
      <div onClick={handleTogglingClick} className={`todo-text ${setComplete()}`} >{todoDetails.name}</div>
      <i id="check" className={`fas fa-check-circle fa-2x`}/>
      {renderDeleteButton()}
      
    </div>
  );
};

export default Todo;
