import React from "react";

const Todo = (props) => {

  const { todoDetails, toggleCompleteStatus, removeTodo, darkMode } = props;

const setTheme = () => {
  if (darkMode) {
    return "dark-mode"
  } else return "light-mode"
}

const setComplete = () => {
  if(todoDetails.complete) {
    return "completed"
  }
}

  const success = () => {
    return (
      <svg xmlns="http://www.w3.org/2000/svg" class="icon icon-tabler icon-tabler-circle-check" width="44" height="44" viewBox="0 0 24 24" stroke-width="1.5" stroke="#8BC34A" fill="none" stroke-linecap="round" stroke-linejoin="round">
  <path stroke="none" d="M0 0h24v24H0z"/>
  <circle cx="12" cy="12" r="9" />
  <path d="M9 12l2 2l4 -4" />
</svg>
    )
  }

  const handleTogglingClick = () => {
    toggleCompleteStatus(todoDetails.id);
  };

  const handleRemoveClick = () => {
    removeTodo(todoDetails.id);
  };

  return (
    <div className={`todos-wrapper ${setTheme()}`}>
      <h3 className={`todo-text ${darkMode} ${setComplete()}`} onClick={handleTogglingClick}>{todoDetails.name}</h3>
      <div className={`complete-status`}/>
      <i className={`btn fas fa-times fa-6 ${darkMode}`} onClick={handleRemoveClick}>
      </i>
    </div>
  );
};

export default Todo;
