import React, { useState, useEffect } from "react";

const Todo = (props) => {
  const [color, setColor] = useState("");
  const [style, setStyle] = useState(null);

  const { todoDetails, toggleCompleteStatus, removeTodo } = props;

  const success = () => {
    return (
      <svg xmlns="http://www.w3.org/2000/svg" class="icon icon-tabler icon-tabler-circle-check" width="44" height="44" viewBox="0 0 24 24" stroke-width="1.5" stroke="#8BC34A" fill="none" stroke-linecap="round" stroke-linejoin="round">
  <path stroke="none" d="M0 0h24v24H0z"/>
  <circle cx="12" cy="12" r="9" />
  <path d="M9 12l2 2l4 -4" />
</svg>
    )
  }

  useEffect(() => {
    if (todoDetails.complete === true) {
      setColor("lightgreen");
      setStyle("complete");
    } else {
      setColor("pink");
      setStyle("incomplete");
    }
  }, [todoDetails.complete]);

  const handleTogglingClick = () => {
    toggleCompleteStatus(todoDetails.id);
  };

  const handleRemoveClick = () => {
    removeTodo(todoDetails.id);
  };

  return (
    <div className="todos-wrapper" style={{ backgroundColor: color }}>
